    public static ArrayList<Marvel> getListData() {
        Marvel marvel = null;
        ArrayList<Marvel> list = new ArrayList<>();
        for (String[] aData : data) {
            marvel = new Marvel();
            marvel.setName(aData[0]);
            marvel.setYear(aData[1]);
            marvel.setPhoto(aData[2]);
            marvel.setIsi(aData[3]);
            marvel.setQuoter(aData[4]);

            list.add(marvel);
        }

        return list;
    }
